SELECT
FROM act_base b LEFT JOIN (SELECT DISTINCT s.act_id from act_stores s RIGHT JOIN (SELECT DISTINCT act_id from act_goods_mapping where sku in
#{sku}
) g on s.act_id = g.act_id where s.store_id = #{storeId}) st ON b.id = st.act_id where b.begin_time < #{now} and b.end_time > #{now} and b.city_id = #{cityId} AND b.act_target LIKE concat("%",#{actTarget},"%") AND st.act_id is NOT NULL AND b.status = 'PUBLISH' ORDER BY b.sort_id

SELECT

FROM act_base
where id IN

    #{id}

and end_time < #{now}
