mapper.update:
UPDATE
    st_credit_money
SET credit_limit_available = credit_limit_available - #{credit}
WHERE credit_limit_available >= #{credit}
AND store_id = (SELECT store_id FROM emp_employee WHERE emp_id = #{userId})

UPDATE st_subvention
SET balance = balance - #{subvention}
WHERE balance >= #{subvention}
AND store_id = (SELECT store_id FROM emp_employee WHERE emp_id = #{userId})

UPDATE st_inventory
SET available_ity = available_ity - #{qty}
WHERE available_ity >= #{qty}
AND gid = #{gid}
AND store_id = (SELECT emp_employee.store_id FROM emp_employee WHERE emp_id = #{userId})

UPDATE st_inventory
 SET available_ity = available_ity - #{qty}
WHERE available_ity >= #{qty}
AND gid = #{gid}
AND store_id = (SELECT cus_customer.store_id FROM cus_customer WHERE cus_id = #{userId})

UPDATE st_inventory
SET available_ity = available_ity + #{qty}
WHERE
gid = #{gid}
AND store_id = (SELECT emp_employee.store_id FROM emp_employee WHERE emp_id = #{userId})

UPDATE st_inventory
SET available_ity = available_ity + #{qty}
WHERE
gid = #{gid}
AND store_id = (SELECT cus_customer.store_id FROM cus_customer WHERE cus_id = #{userId})
