mapper.select:
(SELECT ob.oid orderId, ob.ord_no orderNo, '' returnNo, obd.st_credit_money orderCreditMoney, ob.create_time orderTime, wa.send_time shipmentTime, 'order' orderType from ord_base_info ob INNER JOIN ord_billing_details obd on ob.oid = obd.oid LEFT JOIN bill_repayment_goods_details brgd on brgd.order_no = ob.ord_no left JOIN bill_repayment_info bpi on bpi.id = brgd.repayment_id LEFT JOIN (SELECT * from inter_wta_order_shipping_header where send_flag = 1 GROUP BY order_no) wa on wa.order_no = ob.ord_no LEFT JOIN decoration_company_credit_billing_details dccb on dccb.order_number = ob.ord_no LEFT JOIN decoration_company_credit_billing dc on dc.credit_billing_no = dccb.credit_billing_no where ob.delivery_type = "HOUSE_DELIVERY" and obd.st_credit_money > 0 and ob.`status` in ('PENDING_RECEIVE','FINISHED','REJECTED','CLOSED') and ob.store_id = #{storeId}
AND wa.send_time > #{startTime}
and wa.send_time < #{endTime}
and (bpi.is_paid = 1 OR dc.is_pay_off = 1)
and (bpi.is_paid = 0 OR bpi.is_paid is null) and (dc.is_pay_off = 0 Or dc.is_pay_off is null)
)
UNION ALL
(SELECT ob.oid orderId, ob.ord_no orderNo, '' returnNo, obd.st_credit_money orderCreditMoney, ob.create_time orderTime, os.shipping_time shipmentTime, 'order' orderType from ord_base_info ob INNER JOIN ord_billing_details obd on ob.oid = obd.oid LEFT JOIN bill_repayment_goods_details brgd on brgd.order_no = ob.ord_no left JOIN bill_repayment_info bpi on bpi.id = brgd.repayment_id LEFT JOIN (SELECT * from ord_shipping GROUP BY ord_no) os on os.ord_no = ob.ord_no LEFT JOIN decoration_company_credit_billing_details dccb on dccb.order_number = ob.ord_no LEFT JOIN decoration_company_credit_billing dc on dc.credit_billing_no = dccb.credit_billing_no where ob.delivery_type = "SELF_TAKE" and obd.st_credit_money > 0 and obd.is_pay_up = 1 and ob.`status` in ('FINISHED') and ob.store_id = #{storeId}
AND os.shipping_time > #{startTime}
and os.shipping_time < #{endTime}
and (bpi.is_paid = 1 OR dc.is_pay_off = 1)
and (bpi.is_paid = 0 OR bpi.is_paid is null) and (dc.is_pay_off = 0 Or dc.is_pay_off is null)
)
UNION ALL
(SELECT orb.roid orderId, ob.ord_no orderNo, orb.return_no returnNo, -ABS(orbd.st_credit_money) orderCreditMoney, ob.create_time orderTime, orl.operation_time shipmentTime, 'return' orderType from ord_return_base_info orb INNER JOIN ord_base_info ob on orb.ord_no = ob.ord_no INNER JOIN ord_return_billing orbd on orb.return_no = orbd.return_no LEFT JOIN bill_repayment_goods_details brgd on brgd.order_no = orb.return_no left JOIN bill_repayment_info bpi on bpi.id = brgd.repayment_id LEFT JOIN ord_return_lifecycle orl on orl.return_no = orb.return_no LEFT JOIN decoration_company_credit_billing_details dccb on dccb.order_number = orb.return_no LEFT JOIN decoration_company_credit_billing dc on dc.credit_billing_no = dccb.credit_billing_no where ob.delivery_type in ("SELF_TAKE","HOUSE_DELIVERY") and orl.operation in ('REJECTED','NORMAL_RETURN') and post_status = "FINISHED" and orbd.st_credit_money > 0 and ob.store_id = #{storeId}
AND orl.operation_time > #{startTime}
and orl.operation_time < #{endTime}
and (bpi.is_paid = 1 OR dc.is_pay_off = 1)
and (bpi.is_paid = 0 OR bpi.is_paid is null) and (dc.is_pay_off = 0 Or dc.is_pay_off is null)
)

SELECT repayment_id, repayment_no, order_no, return_no, order_credit_money, order_time, shipment_time, order_type, interest_amount
FROM bill_repayment_goods_details
WHERE repayment_no = #{repaymentNo}

mapper.update:
UPDATE bill_info SET
`status` = #{afterStatus}
WHERE store_id = #{storeId} AND `status` = #{beforeStatus}

mapper.select:
SELECT bill_no, bill_start_date, bill_end_date, repayment_deadline_date, status
FROM bill_info
WHERE store_id = #{storeId} AND `status` = #{status} AND bill_start_date = #{billStartDate}
