mapper.update:
UPDATE city_inventory
SET available_ity = available_ity - #{qty}
WHERE available_ity >= #{qty}
AND gid = #{gid}
AND city_id = (SELECT cus_customer.city_id FROM cus_customer WHERE cus_id = #{userId})

UPDATE city_inventory
SET available_ity = available_ity + #{qty}
WHERE
gid = #{gid}
AND city_id = (SELECT cus_customer.city_id FROM cus_customer WHERE cus_id = #{userId})

UPDATE city_inventory
SET available_ity = available_ity - #{qty}
WHERE available_ity >= #{qty}
AND gid = #{gid}
AND city_id = (SELECT emp_employee.city_id FROM emp_employee WHERE emp_id = #{userId})

UPDATE city_inventory
SET available_ity = available_ity + #{qty}
WHERE
gid = #{gid}
AND city_id = (SELECT emp_employee.city_id FROM emp_employee WHERE emp_id = #{userId})
