mapper.select:
SELECT *
FROM gds_goods
WHERE gid = #{gid}

SELECT *
FROM gds_goods
WHERE sku = #{sku}

mapper.delete:
DELETE FROM gds_goods_collect
WHERE user_id = #{userId}
AND gid = #{goodsId}
AND identity_type = #{type,
    javaType=cn.com.leyizhuang.app.core.constant.AppIdentityType,
    typeHandler=org.apache.ibatis.type.EnumTypeHandler}

mapper.select:
SELECT g.cover_image_uri, g.sku_name goods_name, g.gid id, g.goods_specification, g.goods_unit, p.vip_price, p.retail_price, g.cid category_id, g.brd_id brand_id, g.gtid type_id FROM cus_customer c INNER JOIN gds_goods_price p ON c.store_id = p.store_id AND p.start_time < NOW() AND (p.end_time IS NULL OR p.end_time > NOW()) INNER JOIN gds_goods g ON p.gid = g.gid INNER JOIN gds_goods_category gc ON g.cid = gc.cid WHERE c.cus_id = #{userId}
AND gc.p_category_code = #{firstCategoryCode}
AND g.cid = #{secondCategoryId}
AND g.brd_id = #{brandId}
AND g.gtid = #{typeId}
AND g.goods_specification = #{specification}

SELECT g.cover_image_uri, g.sku_name goods_name, g.gid id, g.goods_specification, g.goods_unit, p.vip_price, p.retail_price, g.cid category_id, g.brd_id brand_id, g.gtid type_id FROM emp_employee e INNER JOIN gds_goods_price p ON e.store_id = p.store_id AND p.start_time < NOW() AND (p.end_time IS NULL OR p.end_time > NOW()) INNER JOIN gds_goods g ON p.gid = g.gid INNER JOIN gds_goods_category gc ON g.cid = gc.cid WHERE e.emp_id = #{userId}
AND gc.p_category_code = #{firstCategoryCode}
AND g.cid = #{secondCategoryId}
AND g.brd_id = #{brandId}
AND g.gtid = #{typeId}
AND g.goods_specification = #{specification}
