SELECT g.gid goods_id, g.sku_name, g.goods_specification, g.cover_image_uri, g.goods_unit, g.sku, gp.retail_price, min(gp.vip_price)AS vip_price FROM cus_customer c LEFT JOIN cus_rank cr ON cr.cus_id = c.cus_id LEFT JOIN rank_classification rc ON cr.rank_id = rc.rank_id INNER JOIN gds_goods_price gp ON c.store_id = gp.store_id AND gp.start_time < NOW() AND (gp.end_time IS NULL OR gp.end_time > NOW()) AND (rc.rank_code = gp.price_type OR gp.price_type = 'COMMON') INNER JOIN gds_goods g ON gp.gid = g.gid WHERE gp.gid IN
#{goodsId}
AND c.cus_id = #{userId} GROUP BY gp.sku

SELECT g.gid goods_id, g.sku_name, g.goods_specification, g.cover_image_uri, g.goods_unit, gp.retail_price, min(gp.vip_price)AS vip_price FROM emp_employee e INNER JOIN gds_goods_price gp ON e.store_id = gp.store_id AND gp.start_time < NOW() AND (gp.end_time IS NULL OR gp.end_time > NOW()) AND gp.price_type = 'COMMON' INNER JOIN gds_goods g ON gp.gid = g.gid WHERE gp.gid IN
#{goodsId}
AND e.emp_id = #{userId} GROUP BY gp.sku

SELECT g.gid goods_id, g.sku_name, g.goods_specification, g.cover_image_uri, g.goods_unit, g.sku, g.company_flag, gp.retail_price FROM cus_customer c LEFT JOIN cus_rank cr ON cr.cus_id = c.cus_id LEFT JOIN rank_classification rc ON cr.rank_id = rc.rank_id INNER JOIN gds_goods_price gp ON c.store_id = gp.store_id AND gp.start_time < NOW() AND (gp.end_time IS NULL OR gp.end_time > NOW()) AND rc.rank_code = gp.price_type INNER JOIN gds_goods g ON gp.gid = g.gid WHERE gp.gid IN
#{goodsId}
AND c.cus_id = #{userId}

