mapper.select:
SELECT g.gid goods_id, g.sku_name, g.goods_specification, g.cover_image_uri, g.goods_unit, gp.retail_price FROM cus_customer c INNER JOIN gds_goods_price gp ON c.store_id = gp.store_id AND gp.start_time < NOW() AND (gp.end_time IS NULL OR gp.end_time > NOW()) INNER JOIN gds_goods g ON gp.gid = g.gid WHERE gp.gid IN
#{goodsId}
AND c.cus_id = #{userId}

SELECT g.gid goods_id, g.sku_name, g.goods_specification, g.cover_image_uri, g.goods_unit, gp.retail_price FROM emp_employee e INNER JOIN gds_goods_price gp ON e.store_id = gp.store_id AND gp.start_time < NOW() AND (gp.end_time IS NULL OR gp.end_time > NOW()) INNER JOIN gds_goods g ON gp.gid = g.gid WHERE gp.gid IN
#{goodsId}
AND e.emp_id = #{userId}
