mapper.select:
select
id,
number,
status,
allocation_from_name,
allocation_to_name,
city_name,
DATE_FORMAT(modify_time,'%Y-%m-%d %H:%i:%s') modify_time
from ity_allocation
WHERE (allocation_from = #{storeId} or allocation_to = #{storeId})

    AND city_id = #{city}


    AND allocation_from = #{formName}


    AND allocation_to = #{toName}


    AND status = #{allocationTypeEnum}


    AND modify_time >= #{startDateTime}


    AND modify_time <= #{endDateTime}

ORDER BY modify_time DESC

mapper.update:
UPDATE inter_ate_allocation_inf


        status = #{flag},


        updated_time = #{sendTime},


        msg = #{errorMsg}


WHERE id in

    #{id}

mapper.select:
select a.id, a.number, a.status, a.allocation_from_name, a.allocation_to_name, a.city_name, DATE_FORMAT(a.modify_time,'%Y-%m-%d %H:%i:%s') modify_time from ity_allocation a LEFT JOIN st_store b ON a.allocation_from = b.store_id LEFT JOIN st_store c ON a.allocation_to = c.store_id LEFT JOIN ity_allocation_detail d on a.id = d.allocation_id WHERE 1=1
and (b.store_structure_code LIKE concat('%',#{company},'%') OR c.store_structure_code LIKE concat('%',#{company},'%'))
and allocation_from = #{outStore}
and allocation_to = #{inStore}
and (d.sku LIKE concat('%',#{keywords},'%') OR d.sku_name LIKE concat('%',#{keywords},'%'))
AND a.status = #{selectStatus}
AND a.modify_time >= #{startDateTime}
AND a.modify_time <= #{endDateTime}
AND ( allocation_from IN
#{item}
OR allocation_to IN
#{item}
) ORDER BY modify_time DESC
