mapper.select:
select id, change_type_desc changType, city_name changeTarget, sku goodsCode, sku_name goodsTitle, change_qty changeValue, DATE_FORMAT(change_time,'%Y-%m-%d %H:%i:%s') changeDate, reference_number referenceOrder from city_inventory_available_qty_change_log WHERE TRUE
AND city_id = #{city}
AND change_time >= #{startDateTime}
AND change_time <= #{endDateTime}
UNION ALL select id, change_type_desc changType, store_name changeTarget, sku goodsCode, sku_name goodsTitle, change_qty changeValue, DATE_FORMAT(change_time,'%Y-%m-%d %H:%i:%s') changeDate, reference_number referenceOrder from st_inventory_available_qty_change_log WHERE TRUE
AND store_id = #{store}
AND change_time >= #{startDateTime}
AND change_time <= #{endDateTime}

select id, change_type_desc changType, store_name changeTarget, sku goodsCode, sku_name goodsTitle, change_qty changeValue, DATE_FORMAT(change_time,'%Y-%m-%d %H:%i:%s') changeDate, reference_number referenceOrder from st_inventory_available_qty_change_log WHERE TRUE
AND store_id = #{store}
AND change_time >= #{startDateTime}
AND change_time <= #{endDateTime}
ORDER BY change_time DESC
