mapper.select:
SELECT c.cus_id, c.name, c.mobile, cp.id, cp.balance, s.store_name FROM cus_customer c LEFT JOIN cus_pre_deposit cp on cp.cus_id = c.cus_id LEFT JOIN st_store s ON s.store_id = c.store_id WHERE (1 = 1)
AND c.city_id = #{cityId}
AND c.store_id = #{storeId}
AND (c.name LIKE concat(concat('%',#{keywords}),'%') OR c.mobile LIKE concat(concat('%',#{keywords}),'%'))
ORDER BY c.create_time DESC

mapper.update:
UPDATE cus_pre_deposit
SET balance = balance + #{deposit},
  last_update_time = #{lastUpdateTime}
WHERE cus_id = #{userId}
 AND last_update_time = #{oldLastUpdateTime}
