SELECT * FROM ( SELECT ol.create_time createTime, oi.ord_no orderNumber, od.st_credit_money creditMoney, s.store_name storeName, oi.creator_name creatorName, IF(og.delivery_type = 'HOUSE_DELIVERY', og.shipping_address, og.booking_store_address) deliveryAddress, og.receiver FROM ord_base_info oi INNER JOIN ord_billing_details od ON oi.ord_no = od.ord_no INNER JOIN ord_delivery_info_details ol ON oi.ord_no = ol.order_no AND ol.logistic_status = 'SEALED_CAR' INNER JOIN st_store s ON s.store_id = oi.store_id INNER JOIN ord_logistics_info og ON og.ord_no = oi.ord_no LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = oi.ord_no WHERE oi.order_subject_type = 'FIT' AND (oi.`status` = 'FINISHED' OR oi.`status` = 'CLOSED' OR oi.`status` = 'CANCELED' OR oi.`status` = 'REJECTED' OR oi.`status` = 'CANCELING') AND od.st_credit_money != 0 AND oi.store_id = #{storeId}
AND ol.create_time > #{startTime}
AND ol.create_time < #{endTime}
AND oi.ord_no LIKE concat(concat('%',#{keywords}),'%')
AND dcd.order_number IS NULL
UNION SELECT ord.create_time createTime, ori.return_no orderNumber, -1*orb.st_credit_money creditMoney, s.store_name storeName, ori.creator_name creatorName, IF(orl.delivery_type = 'HOUSE_PICK', orl.return_full_address, orl.return_store_address) deliveryAddress, orl.rejecter FROM ord_return_base_info ori INNER JOIN ord_return_billing orb ON ori.return_no = orb.return_no INNER JOIN ord_return_delivery_detail ord ON ori.return_no = ord.return_no AND ord.return_logistic_status = 'AGAIN_ON_SALE' INNER JOIN st_store s ON s.store_id = ori.store_id INNER JOIN ord_return_logistic_info orl ON ori.return_no = orl.return_no INNER JOIN ord_base_info oi ON ori.ord_no = oi.ord_no AND oi.order_subject_type = 'FIT' LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = ori.return_no WHERE ori.return_status = 'FINISHED' AND orb.st_credit_money != 0 AND ori.store_id = #{storeId}
AND ord.create_time > #{startTime}
AND ord.create_time < #{endTime}
AND ori.return_no LIKE concat(concat('%',#{keywords}),'%')
AND dcd.order_number IS NULL
) temp GROUP BY orderNumber ORDER BY createTime ASC

SELECT SUM(temp.creditMoney) FROM ( SELECT SUM(od.st_credit_money) creditMoney FROM ord_base_info oi INNER JOIN ord_billing_details od ON oi.ord_no = od.ord_no LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = oi.ord_no WHERE oi.store_id = #{storeId} AND dcd.order_number IS NULL AND oi.ord_no IN
#{item}
UNION SELECT -1*SUM(orb.st_credit_money) creditMoney FROM ord_return_base_info ori INNER JOIN ord_return_billing orb ON ori.return_no = orb.return_no LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = ori.ord_no WHERE ori.store_id = #{storeId} AND dcd.order_number IS NULL AND ori.return_no IN
#{item}
) temp

SELECT * FROM ( SELECT ol.create_time orderTime, oi.ord_no orderNumber, od.st_credit_money creditMoney, oi.store_id storeId, oi.creator_name creatorName, oi.creator_id creatorId, IF(og.delivery_type = 'HOUSE_DELIVERY', og.shipping_address, og.booking_store_address) deliveryAddress, og.receiver,oi.remark as remark, SUM(IFNULL(ogi.order_qty, 0)) goodsQty, SUM(IFNULL(ogi.wholesale_price * ogi.order_qty, 0)) as accountMoney, og.estate_info as estateInfo FROM ord_base_info oi INNER JOIN ord_billing_details od ON oi.ord_no = od.ord_no INNER JOIN ord_delivery_info_details ol ON oi.ord_no = ol.order_no AND ol.logistic_status = 'SEALED_CAR' INNER JOIN ord_logistics_info og ON og.ord_no = oi.ord_no INNER JOIN ord_goods_info ogi ON ogi.ord_no = oi.ord_no LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = oi.ord_no WHERE oi.order_subject_type = 'FIT' AND (oi.`status` = 'FINISHED' OR oi.`status` = 'CLOSED' OR oi.`status` = 'CANCELED' OR oi.`status` = 'REJECTED' OR oi.`status` = 'CANCELING') AND od.st_credit_money != 0 AND oi.store_id = #{storeId} AND dcd.order_number IS NULL AND oi.ord_no IN
#{item}
GROUP BY oi.ord_no UNION SELECT ord.create_time createTime, ori.return_no orderNumber, -1 * IFNULL(orb.st_credit_money,0) creditMoney, ori.store_id storeId, ori.creator_name creatorName, ori.creator_id creatorId, IF(orl.delivery_type = 'HOUSE_PICK', orl.return_full_address, orl.return_store_address) deliveryAddress, orl.rejecter,ori.reason_info as remark, -1 * SUM(IFNULL(orgi.return_qty, 0)) goodsQty, -1 * SUM(IFNULL(orgi.wholesale_price * orgi.return_qty, 0)) as accountMoney, '' as estateInfo FROM ord_return_base_info ori INNER JOIN ord_return_billing orb ON ori.return_no = orb.return_no INNER JOIN ord_return_delivery_detail ord ON ori.return_no = ord.return_no AND ord.return_logistic_status = 'AGAIN_ON_SALE' INNER JOIN ord_return_logistic_info orl ON ori.return_no = orl.return_no INNER JOIN ord_base_info oi ON ori.ord_no = oi.ord_no AND oi.order_subject_type = 'FIT' INNER JOIN ord_return_goods_info orgi ON orgi.return_no = ori.return_no LEFT JOIN decoration_company_credit_billing_details dcd ON dcd.order_number = ori.return_no WHERE ori.return_status = 'FINISHED' AND orb.st_credit_money != 0 AND ori.store_id = #{storeId} AND dcd.order_number IS NULL AND ori.return_no IN
#{item}
GROUP BY orderNumber ) temp WHERE temp.orderNumber IS NOT null GROUP BY orderNumber

SELECT id, store_name, CONCAT(DATE_FORMAT(start_time,'%y/%m/%d'),'~', DATE_FORMAT(end_time,'%y/%m/%d')) cycleTime, bill_name, bill_amount, repaid_amount,
  is_pay_off, credit_billing_no, create_time
FROM decoration_company_credit_billing
WHERE 1=1

    AND store_id = #{storeId}


      AND create_time > #{startTime}


      AND create_time < #{endTime}


      AND is_pay_off = #{isPayOff}


      AND credit_billing_no LIKE concat(concat('%',#{keywords}),'%')

  ORDER BY create_time DESC

SELECT id, store_id, bill_amount, repaid_amount, is_pay_off, credit_billing_no, pay_off_operation_id
FROM decoration_company_credit_billing
WHERE id = #{id}

SELECT id, store_id, bill_amount, repaid_amount, is_pay_off, credit_billing_no, pay_off_operation_id
FROM decoration_company_credit_billing
WHERE credit_billing_no = #{creditBillingNo}
