mapper.select:
select e.emp_id,e.login_name,e.name,e.identity_type,e.status,c.city_id,c.name cname,s.store_id,s.store_name from emp_employee e LEFT JOIN city c on e.city_id = c.city_id LEFT JOIN st_store s ON e.store_id = s.store_id where e.store_id = #{storeId}
AND e.identity_type=#{identityType}
AND e.status=#{enabled}
AND e.identity_type!='DECORATE_MANAGER' AND e.identity_type!='DECORATE_EMPLOYEE' order by emp_id asc

select e.emp_id,e.login_name,e.name,e.identity_type,e.status,c.city_id,c.name cname,s.store_id,s.store_name from emp_employee e LEFT JOIN city c on e.city_id = c.city_id LEFT JOIN st_store s ON e.store_id = s.store_id where 1=1
AND e.city_id = #{cityId}
AND e.identity_type=#{identityType}
AND e.status=#{enabled}
AND e.identity_type!='DECORATE_MANAGER' AND e.identity_type!='DECORATE_EMPLOYEE' order by emp_id asc

select e.emp_id,e.login_name,e.name,e.identity_type,e.status,c.city_id,c.name cname,s.store_id,s.store_name from emp_employee e LEFT JOIN city c on e.city_id = c.city_id LEFT JOIN st_store s ON e.store_id = s.store_id where (e.identity_type='DECORATE_MANAGER' OR e.identity_type='DECORATE_EMPLOYEE')
AND e.store_id = #{diyId}
AND e.identity_type=#{identityType}
AND e.status=#{enabled}
order by emp_id asc

select e.login_name ,e.name,c.city_id,c.name cname,s.store_id, s.store_name,e.emp_id,ecm.id,ecm.credit_limit_available ,ecm.temp_credit_limit from emp_employee e LEFT JOIN city c on e.city_id = c.city_id LEFT JOIN st_store s ON e.store_id = s.store_id RIGHT JOIN emp_credit_money ecm ON e.emp_id =ecm.emp_id WHERE 1=1
AND c.city_id=#{cityId}
AND s.store_id=#{storeId}
ORDER BY ecm.id ASC

SELECT emp_id,identity_type
FROM emp_employee
WHERE emp_id = #{id}

SELECT ee.emp_id, ee.`name`, ee.mobile, ss.store_type, ss.store_code, spd.balance FROM emp_employee AS ee INNER JOIN st_store AS ss ON ee.store_id = ss.store_id INNER JOIN st_pre_deposit AS spd ON spd.store_id = ee.store_id WHERE 1 = 1
AND ee.city_id = #{cityId}
AND ee.store_id = #{storeId}

SELECT ee.emp_id, ee.`name`, ee.mobile, ss.store_type, spd.balance FROM emp_employee AS ee INNER JOIN st_store AS ss ON ee.store_id = ss.store_id INNER JOIN st_pre_deposit AS spd ON spd.store_id = ee.store_id WHERE 1 = 1 AND (ee.`name` LIKE CONCAT('%' ,#{sellerQueryConditions},'%') OR ee.mobile LIKE CONCAT('%' ,#{sellerQueryConditions},'%') )
AND ee.city_id = #{cityId}
AND ee.store_id = #{storeId}
