mapper.select:
SELECT obi.ord_no orderNumber, obi.create_time createTime, obi.creator_phone meberNumber, obi.creator_name meberName, obi.`status` 'status', obi.delivery_type deliveryType, s.store_name storeName, obd.amount_payable orderPrice FROM ord_base_info AS obi LEFT JOIN ord_billing_details AS obd ON obi.ord_no = obd.ord_no LEFT JOIN ord_logistics_info AS oli ON obi.ord_no = oli.ord_no LEFT JOIN st_store AS s ON s.store_code = obi.store_code WHERE obi.order_subject_type = 'STORE'
AND obi.delivery_type = #{deliveryType}
AND obi.city_id = #{cityId}
AND obi.store_id = #{storeId}
ORDER BY obi.create_time DESC

SELECT obi.ord_no orderNumber, obi.create_time createTime, obi.creator_phone meberNumber, obi.creator_name meberName, obi.`status` 'status', obi.delivery_type deliveryType, s.store_name storeName, obd.amount_payable orderPrice FROM ord_base_info AS obi LEFT JOIN ord_billing_details AS obd ON obi.ord_no = obd.ord_no LEFT JOIN ord_logistics_info AS oli ON obi.ord_no = oli.ord_no LEFT JOIN st_store AS s ON s.store_code = obi.store_code WHERE obi.order_subject_type = 'STORE'
AND obi.delivery_type = #{appDeliveryType}
AND obi.city_id = #{cityId}
AND obi.store_id = #{storeId}
AND obi.create_time >= #{beginTime}
AND obi.create_time <= #{endTime}
AND obi.customer_name LIKE concat(concat('%',#{memberName}),'%')
AND oli.shipping_address LIKE concat(concat('%',#{shippingAddress}),'%')
AND obi.sales_consult_name LIKE concat(concat('%',#{sellerName}),'%')
AND obi.customer_phone LIKE concat(concat('%',#{memberPhone}),'%')
AND oli.receiver LIKE concat(concat('%',#{receiverName}),'%')
AND oli.receiver_phone LIKE concat(concat('%',#{receiverPhone}),'%')
ORDER BY obi.create_time DESC

SELECT obi.ord_no orderNumber, obi.create_time createTime, obi.creator_phone meberNumber, obi.creator_name meberName, obi.`status` 'status', obi.delivery_type deliveryType, s.store_name storeName, obd.amount_payable orderPrice FROM ord_base_info AS obi LEFT JOIN ord_billing_details AS obd ON obi.ord_no = obd.ord_no LEFT JOIN ord_logistics_info AS oli ON obi.ord_no = oli.ord_no LEFT JOIN st_store AS s ON s.store_code = obi.store_code WHERE obi.order_subject_type = 'FIT'
AND obi.city_id = #{cityId}
AND obi.store_id = #{storeId}
AND obi.create_time >= #{beginTime}
AND obi.create_time <= #{endTime}
AND obi.creator_name LIKE concat(concat('%',#{creatorName}),'%')
AND oli.shipping_address LIKE concat(concat('%',#{shippingAddress}),'%')
AND obi.creator_phone LIKE concat(concat('%',#{creatorPhone}),'%')
AND oli.receiver LIKE concat(concat('%',#{receiverName}),'%')
AND oli.receiver_phone LIKE concat(concat('%',#{receiverPhone}),'%')
ORDER BY obi.create_time DESC

SELECT obi.ord_no orderNumber, obi.create_time createTime, obi.creator_phone meberNumber, obi.creator_name meberName, obi.`status` 'status', obi.delivery_type deliveryType, s.store_name storeName, obd.amount_payable orderPrice FROM ord_base_info AS obi LEFT JOIN ord_billing_details AS obd ON obi.ord_no = obd.ord_no LEFT JOIN ord_logistics_info AS oli ON obi.ord_no = oli.ord_no LEFT JOIN st_store AS s ON s.store_code = obi.store_code WHERE obi.`status` = 'PENDING_SHIPMENT' AND obi.delivery_type = 'SELF_TAKE'
AND obi.city_id = #{cityId}
AND obi.store_id = #{storeId}
AND obi.create_time >= #{beginTime}
AND obi.create_time <= #{endTime}
AND obi.creator_name LIKE concat(concat('%',#{creatorName}),'%')
AND oli.shipping_address LIKE concat(concat('%',#{shippingAddress}),'%')
AND obi.creator_phone LIKE concat(concat('%',#{creatorPhone}),'%')
AND oli.receiver LIKE concat(concat('%',#{receiverName}),'%')
AND oli.receiver_phone LIKE concat(concat('%',#{receiverPhone}),'%')
ORDER BY obi.create_time DESC

mapper.update:
UPDATE ord_base_info
SET
status = #{status}
WHERE ord_no = #{orderNo}

mapper.select:
SELECT oaa.order_money - oaa.real_money
FROM ord_arrears_audit oaa
WHERE oaa.ord_no = #{orderNumber}

mapper.update:
UPDATE ord_arrears_audit
SET whether_repayments = 1,
repayment_time = #{repaymentTime}
WHERE
ord_no = #{orderNumber}
AND `status` = 'AUDIT_PASSED'
