mapper.select:
SELECT p.id, p.photos, p.remark, p.`status`, p.`status` statusEnum, p.create_time, p.photo_order_no, IFNULL(e.`name`,c.`name`) username, IFNULL(e.mobile,c.mobile) userMobile, IFNULL(e.identity_type,'CUSTOMER') identityType, s.store_name FROM photo_order p LEFT JOIN cus_customer c ON c.cus_id = p.user_id AND p.identity_type = 'CUSTOMER' LEFT JOIN emp_employee e ON e.emp_id = p.user_id AND p.identity_type = e.identity_type LEFT JOIN st_store s ON c.store_id = s.store_id OR e.store_id = s.store_id WHERE (1 = 1)
AND s.city_id = #{cityId}
AND s.store_id = #{storeId}
AND p.status = #{status}
AND (p.photo_order_no LIKE concat(concat('%',#{keywords}),'%') OR s.store_name LIKE concat(concat('%',#{keywords}),'%') OR e.mobile LIKE concat(concat('%',#{keywords}),'%') OR c.mobile LIKE concat(concat('%',#{keywords}),'%'))
AND s.store_id IN
#{item}
ORDER BY p.create_time DESC

mapper.update:
UPDATE photo_order
SET status = #{status},
  update_time = now()
WHERE id = #{id}
  AND status != #{status}

UPDATE photo_order
SET operation_user_id = #{userId}
WHERE id = #{id}

