(SELECT s.city as 'cityName', s.store_code as 'storeCode', s.store_name as 'storeName', e.`name` as 'name', ob.customer_name as 'customerName', ob.ord_no as 'ordNo', '自提' as 'orderType', CASE ob.`status` WHEN 'PENDING_RECEIVE' THEN '待收货' WHEN 'FINISHED' THEN '已完成' end as 'orderStatus', os.shipping_time as 'selfTakeOrderTime', ob.create_time as 'createTime', CASE os.shipping_time WHEN null THEN '一代数据' ELSE os.shipping_time END as 'shippingDate', CASE obd.is_pay_up WHEN 0 THEN '未结清' WHEN 1 THEN '结清' end as 'isPayUp', CASE obd.is_pay_up WHEN 0 THEN null WHEN 1 THEN obd.pay_up_time end as 'payUpTime', og.sku as 'sku', og.sku_name as 'skuName', CASE og.goods_line_type WHEN 'PRODUCT_COUPON' THEN '产品券' WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'goodsType', og.company_flag as 'companyFlag',
if(og.goods_line_type = "PRODUCT_COUPON" ,purchase_price , (og.order_qty ) * og.return_price ) as 'financialSales', if(og.goods_line_type = "PRODUCT_COUPON" ,(oci.purchase_price - (og.settlement_price - og.wholesale_price)) , (og.order_qty ) * (og.return_price - (og.settlement_price - og.wholesale_price)) ) as 'distributionSales', og.wholesale_price as 'wholesalePrice', og.retail_price as 'retailPrice', og.settlement_price as 'settlementPrice', (og.retail_price - og.settlement_price) as 'memberDiscount', og.promotion_share_price as 'promotionSharePrice', og.cash_coupon_share_price as 'cashCouponSharePrice', CASE og.goods_line_type WHEN 'PRODUCT_COUPON' THEN 1 ELSE og.order_qty END as 'orderQty', CASE og2.goods_line_type WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'couponType' FROM ord_base_info ob LEFT JOIN ord_goods_info og on og.oid = ob.oid LEFT JOIN st_store s on ob.store_id = s.store_id LEFT JOIN emp_employee e on ob.sales_consult_id = e.emp_id LEFT JOIN ord_billing_details obd on ob.oid = obd.oid LEFT JOIN ord_shipping os on ob.oid = os.oid LEFT JOIN ord_coupon_info oci on ob.oid = oci.oid and og.sku =oci.sku and og.goods_line_type = 'PRODUCT_COUPON' LEFT JOIN cus_customer_product_coupon ccpc on ccpc.id = oci.coupon_id LEFT JOIN ord_goods_info og2 on og2.id = ccpc.goods_line_id WHERE ob.`status` IN ( 'FINISHED' )
AND os.shipping_time >= #{startTime}
and os.shipping_time <= #{endTime}
and obd.pay_up_time < #{startTime}
AND ob.delivery_type = 'SELF_TAKE' and obd.is_pay_up = 1
and (s.store_type in ('ZS') or s.store_structure_code like concat("%|JZC001|%") )
and s.store_type in ('FX','FXCK')
and s.store_type =#{storeType}
AND ob.store_id IN
#{item}
order by ob.store_id,ob.sales_consult_id,ob.create_time)
UNION ALL
(SELECT s.city as 'cityName', s.store_code as 'storeCode', s.store_name as 'storeName', e.`name` as 'name', ob.customer_name as 'customerName', ob.ord_no as 'ordNo', '' as 'orderType', CASE ob.`status` WHEN 'PENDING_RECEIVE' THEN '待收货' WHEN 'FINISHED' THEN '已完成' end as 'orderStatus', '' as 'selfTakeOrderTime', ob.create_time as 'createTime', '' as 'shippingDate', CASE obd.is_pay_up WHEN 0 THEN '未结清' WHEN 1 THEN '结清' end as 'isPayUp', CASE obd.is_pay_up WHEN 0 THEN null WHEN 1 THEN obd.pay_up_time end as 'payUpTime', org.sku as 'sku', org.sku_name as 'skuName', CASE org.goods_line_type WHEN 'PRODUCT_COUPON' THEN '产品券' WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'goodsType', org.company_flag as 'companyFlag', -ABS(org.return_price * org.return_qty) as 'financialSales', -ABS(org.wholesale_price * org.return_qty) as 'distributionSales', -ABS(org.wholesale_price) as 'wholesalePrice', 0 as 'retailPrice', 0 as 'settlementPrice', 0 as 'memberDiscount', 0 as 'promotionSharePrice', 0 as 'cashCouponSharePrice', CASE org.goods_line_type WHEN 'PRODUCT_COUPON' THEN -1 ELSE -ABS(org.return_qty) END as 'orderQty', CASE og2.goods_line_type WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'couponType' FROM ord_return_base_info orb LEFT JOIN ord_base_info ob on orb.oid = ob.oid LEFT JOIN ord_return_goods_info org on orb.roid = org.roid LEFT JOIN st_store s on ob.store_id = s.store_id LEFT JOIN emp_employee e on ob.sales_consult_id = e.emp_id LEFT JOIN inter_wta_return_order_back_header wro ON wro.po_no = orb.return_no LEFT JOIN ord_billing_details obd on ob.oid = obd.oid LEFT JOIN ord_coupon_info oci on ob.oid = oci.oid and org.sku = oci.sku and org.goods_line_type = 'PRODUCT_COUPON' LEFT JOIN cus_customer_product_coupon ccpc on ccpc.id = oci.coupon_id LEFT JOIN ord_goods_info og2 on og2.id = ccpc.goods_line_id where 1=1 and ob.`status` IN ('FINISHED')
AND wro.create_time > #{startTime}
and wro.create_time <= #{endTime}
and (s.store_type in ('ZS') or s.store_structure_code like concat("%|JZC001|%") )
and s.store_type in ('FX','FXCK')
and s.store_type =#{storeType}
AND ob.store_id IN
#{item}
)
UNION ALL
(SELECT s.city as 'cityName', s.store_code as 'storeCode', s.store_name as 'storeName', e.`name` as 'name', ob.customer_name as 'customerName', ob.ord_no as 'ordNo', '' as 'orderType', CASE ob.`status` WHEN 'PENDING_RECEIVE' THEN '待收货' WHEN 'FINISHED' THEN '已完成' end as 'orderStatus', '' as 'selfTakeOrderTime', ob.create_time as 'createTime', '' as 'shippingDate', CASE obd.is_pay_up WHEN 0 THEN '未结清' WHEN 1 THEN '结清' end as 'isPayUp', CASE obd.is_pay_up WHEN 0 THEN null WHEN 1 THEN obd.pay_up_time end as 'payUpTime', org.sku as 'sku', org.sku_name as 'skuName', CASE org.goods_line_type WHEN 'PRODUCT_COUPON' THEN '产品券' WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'goodsType', org.company_flag as 'companyFlag', -ABS(org.return_price * org.return_qty) as 'financialSales', -ABS(org.wholesale_price * org.return_qty) as 'distributionSales', -ABS(org.wholesale_price) as 'wholesalePrice', 0 as 'retailPrice', 0 as 'settlementPrice', 0 as 'memberDiscount', 0 as 'promotionSharePrice', 0 as 'cashCouponSharePrice', CASE org.goods_line_type WHEN 'PRODUCT_COUPON' THEN -1 ELSE -ABS(org.return_qty) END as 'orderQty', CASE og2.goods_line_type WHEN 'GOODS' THEN '本品' WHEN 'PRESENT' THEN '赠品' END as 'couponType' FROM ord_return_base_info orb LEFT JOIN ord_base_info ob on orb.oid = ob.oid LEFT JOIN ord_return_goods_info org on orb.roid = org.roid LEFT JOIN st_store s on ob.store_id = s.store_id LEFT JOIN emp_employee e on ob.sales_consult_id = e.emp_id
LEFT JOIN ord_billing_details obd on ob.oid = obd.oid LEFT JOIN ord_coupon_info oci on ob.oid = oci.oid and org.sku = oci.sku and org.goods_line_type = 'PRODUCT_COUPON' LEFT JOIN cus_customer_product_coupon ccpc on ccpc.id = oci.coupon_id LEFT JOIN ord_goods_info og2 on og2.id = ccpc.goods_line_id where 1=1 AND ob.delivery_type = 'SELF_TAKE' and orb.return_type = 'NORMAL_RETURN' and orb.return_status = 'FINISHED'
AND orb.return_time > #{startTime}
and orb.return_time <= #{endTime}
and (s.store_type in ('ZS') or s.store_structure_code like concat("%|JZC001|%") )
and s.store_type in ('FX','FXCK')
and s.store_type =#{storeType}
AND ob.store_id IN
#{item}
)

SELECT DISTINCT obi.city_name cityName, st.store_name storeName, ol.operation_time orderTime, obi.ord_no orderNumber, null returnNumber, obi.customer_name customerName, obi.sales_consult_name sellerName, obi.delivery_type deliveryType, oli.receiver receiver, oli.receiver_phone receiverPhone, (CASE WHEN obi.`status` = 'FINISHED' OR obi.`status` = 'CLOSED' OR obi.`status` = 'REJECTED' OR (obi.`status` = 'PENDING_RECEIVE' AND obi.delivery_type = 'HOUSE_DELIVERY')THEN '已出货' WHEN obi.`status` = 'CANCELED' OR obi.`status` = 'CANCELING' OR obi.`status` = 'PENDING_SHIPMENT' OR (obi.`status` = 'PENDING_RECEIVE' AND obi.delivery_type = 'SELF_TAKE') THEN '未出货' ELSE '未知' END)deliveryStatus, oli.shipping_address, og.sku sku, og.sku_name skuName, (CASE WHEN og.company_flag = 'HR' THEN '华润' WHEN og.company_flag = 'LYZ' THEN '乐意装' WHEN og.company_flag = 'YR' THEN '莹润' ELSE '其他' END)companyFlag, og.goods_line_type goodsLineType, IFNULL(og.order_qty, 0) quantity, IFNULL(og.settlement_price, 0) settlementPrice, (IFNULL(og.order_qty, 0) * IFNULL(og.settlement_price, 0)) settlementTotlePrice, IFNULL(og.return_price, 0) promotionSharePrice,(IFNULL(og.order_qty, 0) * IFNULL(og.return_price, 0)) promotionShareTotlePrice, obi.customer_phone customerPhone, st.company_name companyName FROM ord_base_info obi LEFT JOIN ord_goods_info og ON og.ord_no = obi.ord_no INNER JOIN rank_store st ON st.store_id = obi.store_id INNER JOIN rank_goods rg ON rg.gid = og.gid LEFT JOIN ord_lifecycle ol ON obi.ord_no = ol.ord_no LEFT JOIN ord_logistics_info oli ON oli.ord_no = obi.ord_no WHERE obi.`status` != 'UNPAID' AND (ol.post_status = 'PENDING_SHIPMENT' OR (ol.post_status = 'PENDING_RECEIVE' AND obi.delivery_type = 'SELF_TAKE'))
AND obi.city_id = #{cityId}
AND obi.store_id = #{storeId}
AND ol.operation_time >= #{startTime}
AND ol.operation_time <= #{endTime}
AND obi.ord_no LIKE concat(concat('%',#{keywords}),'%')
AND obi.store_id IN
#{item}
UNION SELECT DISTINCT obi.city_name cityName, st.store_name storeName, rl.operation_time orderTime, obi.ord_no orderNumber, orbi.return_no returnNumber, orbi.cus_name customerName, obi.sales_consult_name sellerName, obi.delivery_type deliveryType, orli.rejecter receiver, orli.rejecter_phone receiverPhone, (CASE WHEN orbi.return_type = 'REFUSED_RETURN' OR orbi.return_type = 'NORMAL_RETURN' THEN '已取货' WHEN orbi.return_type = 'CANCEL_RETURN' THEN '订单取消' ELSE '未知' END) deliveryStatus, orli.return_full_address, org.sku sku, org.sku_name skuName, (CASE WHEN org.company_flag = 'HR' THEN '华润' WHEN org.company_flag = 'LYZ' THEN '乐意装' WHEN org.company_flag = 'YR' THEN '莹润' ELSE '其他' END) companyFlag, org.goods_line_type goodsLineType, (-1 * IFNULL(org.return_qty,0)) quantity, IFNULL(org.settlement_price, 0) settlementPrice, (-1 * IFNULL(org.return_qty, 0) * IFNULL(org.settlement_price, 0)) settlementTotlePrice, IFNULL(org.return_price, 0) promotionSharePrice, (-1 * IFNULL(org.return_qty, 0) * IFNULL(org.return_price, 0)) promotionShareTotlePrice, obi.customer_phone customerPhone, st.company_name companyName FROM ord_return_base_info orbi LEFT JOIN ord_return_goods_info org ON org.return_no = orbi.return_no INNER JOIN rank_goods rg ON rg.sku = org.sku INNER JOIN rank_store st ON st.store_id = orbi.store_id LEFT JOIN ord_return_lifecycle rl ON orbi.return_no = rl.return_no LEFT JOIN ord_base_info obi ON obi.ord_no = orbi.ord_no LEFT JOIN ord_return_logistic_info orli ON orli.return_no = orbi.return_no WHERE orbi.return_status = 'FINISHED' AND rl.post_status = 'FINISHED'
AND obi.city_id = #{cityId}
AND orbi.store_id = #{storeId}
AND rl.operation_time >= #{startTime}
AND rl.operation_time <= #{endTime}
AND orbi.return_no LIKE concat(concat('%',#{keywords}),'%')
AND orbi.store_id IN
#{item}
ORDER BY orderTime DESC
