mapper.select:
SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, s.store_org_id, c.name FROM st_store s LEFT JOIN city c ON s.city_code = c.code WHERE s.store_type!="ZS" AND s.store_id IN
#{item}
ORDER BY create_time DESC

SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, s.store_org_id, c.name FROM st_store s LEFT JOIN city c ON s.city_code = c.code WHERE 1=1
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
AND s.store_type!="ZS" AND s.store_id IN
#{item}

SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, s.create_time, c.name, ee.name sales_manager FROM st_store s LEFT JOIN city c ON s.city_code = c.code LEFT JOIN emp_employee ee ON ee.emp_id = s.sales_manager WHERE s.store_type="ZS"
AND s.fit_compay_type = #{fitCompayType}
AND ( s.store_code like CONCAT('%', #{keywords}, '%') OR s.store_name like CONCAT('%', #{keywords}, '%') )
AND s.city_id =#{cityId}
AND s.store_id IN
#{item}

SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, c.name FROM st_store s LEFT JOIN city c ON s.city_code = c.code WHERE s.store_type="ZS"
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
ORDER BY s.create_time DESC

SELECT s.store_code, s.store_name, s.enable, s.store_id, c.name cityName, scm.credit_limit_available AS credit, ss.balance AS sponsorship, scm.credit_limit FROM st_store s LEFT JOIN city c ON s.city_code = c.code LEFT JOIN st_credit_money scm on s.store_id=scm.store_id LEFT JOIN st_subvention ss on s.store_id=ss.store_id WHERE s.store_type="ZS"
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
ORDER BY s.create_time DESC

SELECT sp.id, sp.balance, s.store_name, s.store_id, s.store_code, c.name city, s.store_type FROM st_store s LEFT JOIN st_pre_deposit sp on sp.store_id = s.store_id LEFT JOIN city c ON c.code = s.city_code WHERE (1 = 1)
AND c.city_id = #{cityId}
AND s.store_type = #{storeType}
AND (s.store_code LIKE concat(concat('%',#{keywords}),'%') OR s.store_name LIKE concat(concat('%',#{keywords}),'%'))
AND s.store_id IN
#{item}
ORDER BY s.create_time DESC

SELECT *
FROM st_pre_deposit
WHERE store_id = #{storeId}

mapper.update:
UPDATE st_pre_deposit
SET balance = balance + #{money},
last_update_time = #{lastUpdateTime}
WHERE store_id = #{storeId}

    AND last_update_time = #{oldUpdateTime}

