mapper.select:
SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, c.name FROM st_store s LEFT JOIN city c ON s.city_code = c.code WHERE 1=1
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
AND s.store_type!="ZS"

SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, c.name FROM st_store s LEFT JOIN city c ON s.city_code = c.code WHERE s.store_type="ZS"
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
ORDER BY s.create_time DESC

SELECT s.store_code, s.store_name, s.enable, s.store_type, s.store_id, c.name, scm.credit_limit_available AS credit, ss.balance AS sponsorship FROM st_store s LEFT JOIN city c ON s.city_code = c.code LEFT JOIN st_credit_money scm on s.store_id=scm.store_id LEFT JOIN st_subvention ss on s.store_id=ss.store_id WHERE s.store_type="ZS"
AND c.city_id= #{cityId}
AND s.enable=#{enabled}
ORDER BY s.create_time DESC
