mapper.select:
SELECT l.id, s.store_name, s.store_code, c.name city, s.store_type, l.create_time, (CASE WHEN l.change_type = 'PLACE_ORDER' OR l.change_type = 'JX_PRICE_DIFFERENCE_DEDUCTION' THEN (-1 * IFNULL(l.change_money, 0)) ELSE IFNULL(l.change_money, 0) END) changeMoney, l.remarks, l.change_type, l.order_number, l.balance, l.transfer_time, l.merchant_order_number, au.`name` AS operatorName FROM st_pre_deposit_log l LEFT JOIN st_store s on l.store_id = s.store_id LEFT JOIN city c ON c.code = s.city_code LEFT JOIN admin_user au ON au.uid = l.operator_id WHERE (1 = 1)
AND s.store_type = #{storeType}
AND c.city_id = #{cityId}
AND s.store_id = #{storeId}
AND l.change_type = #{changeType}
AND (s.store_code LIKE concat(concat('%',#{keywords}),'%') OR s.store_name LIKE concat(concat('%',#{keywords}),'%') OR l.order_number LIKE concat(concat('%',#{keywords}),'%'))
AND s.store_id IN
#{item}
ORDER BY l.create_time DESC
