mapper.select:
SELECT AUDIT_HEADER_ID, AUDIT_NO, DELIVERY_CITY, DELIVERY_COUNTY, DELIVERY_STREET, RESIDENCE_NAME, DETAILED_ADDRESS, STATUS, IS_AUDITED FROM material_audit_sheet WHERE EMPLOYEE_ID = #{employeeID}
AND STATUS = #{status}
ORDER BY AUDIT_HEADER_ID DESC

SELECT AUDIT_HEADER_ID, EMPLOYEE_ID, EMPLOYEE_NAME, DELIVERY_TYPE, STORE_ID, RECEIVER, RECEIVER_PHONE, DELIVERY_CITY, DELIVERY_COUNTY, DELIVERY_STREET, RESIDENCE_NAME, DETAILED_ADDRESS, IS_OWNER_RECEIVING, REMARK, STATUS, RESERVATION_DELIVERY_TIME, CREATE_TIME, AUDIT_NO, AUDITOR_ID, IS_AUDITED FROM material_audit_sheet WHERE STORE_ID = #{storeID}
AND STATUS = #{status}
ORDER BY AUDIT_HEADER_ID DESC
