mapper.update:
UPDATE material_list
SET qty = #{qty}
WHERE id = #{id};

mapper.select:
SELECT *
FROM
material_list
WHERE user_id = #{userId}
AND identity_type = #{identityType}
AND gid = #{goodsId}

SELECT *
FROM
material_list
WHERE user_id = #{userId}
AND identity_type = #{identityType}

SELECT DISTINCT gid
FROM material_list
WHERE user_id = #{userId}
AND identity_type = #{identityType}

mapper.delete:
DELETE FROM material_list WHERE user_id = #{userId} AND identity_type = #{identityType}
AND gid in

    #{id}
