mapper.select:
SELECT l.id, l.change_type type, l.create_time, l.change_money, l.order_number FROM st_pre_deposit_log l INNER JOIN emp_employee e ON e.store_id = l.store_id AND e.emp_id = #{userId} WHERE l.change_type in
#{type}
ORDER BY l.create_time DESC

mapper.update:
UPDATE st_pre_deposit
SET
balance = #{money}
WHERE
    store_id = #{userId}

mapper.select:
SELECT l.id, l.change_type type, l.create_time, l.change_money, l.order_number FROM st_pre_deposit_log l INNER JOIN emp_employee e ON e.store_id = l.store_id AND e.emp_id = #{userId} INNER JOIN st_store s ON s.store_id = l.store_id AND store_type!= 'ZS' WHERE l.change_type in
#{type}
ORDER BY l.create_time DESC

mapper.update:
UPDATE st_pre_deposit
SET
balance = #{money}
WHERE
store_id = #{storeId}
AND last_update_time = #{version}
