mapper.insert:
INSERT INTO inter_wta_return_order_back_goods (gcode, rec_id, rec_no, rec_qty)
    SELECT
        #{gcode},
        #{recId},
        #{recNo},
        #{recQty}
    FROM DUAL
    WHERE NOT EXISTS(
            SELECT 'X'
            FROM inter_wta_return_order_back_goods
            WHERE rec_id = #{recId} AND rec_no = #{recNo}
    );

INSERT INTO inter_wta_return_order_delivery_clerk (create_time, return_number, c_wh_no, driver, note)
    SELECT
        #{createTime},
        #{returnNo},
        #{warehouseNo},
        #{driver},
        #{note}
    FROM DUAL
    WHERE NOT EXISTS(
            SELECT 'X'
            FROM inter_wta_return_order_delivery_clerk
            WHERE return_number = #{returnNo} AND driver = #{driver}
    );

mapper.update:
UPDATE inter_wta_return_order_delivery_clerk


        create_time = #{createTime},


        c_wh_no = #{warehouseNo},


        driver = #{driver},


        note = #{note}


WHERE return_number = #{returnNo}

mapper.insert:
INSERT INTO inter_wta_order_result_enter (create_time, order_no, is_cancel, error_message)

    SELECT
        #{createTime},
        #{orderNo},
        #{isCancel},
        #{errorMessage}
    FROM DUAL
    WHERE NOT EXISTS(
            SELECT 'X'
            FROM inter_wta_order_result_enter
            WHERE order_no = #{orderNo}
    );

INSERT INTO inter_wta_return_order_result_enter (create_time, return_number, is_cancel, error_message)
    SELECT
        #{createTime},
        #{returnNumber},
        #{isCancel},
        #{errorMessage}
    FROM DUAL
    WHERE NOT EXISTS(
            SELECT 'X'
            FROM inter_wta_return_order_result_enter
            WHERE return_number = #{returnNumber}
    );

INSERT INTO inter_wta_warehouse_purchase_goods (rec_id, rec_no, rec_qty, sku)
    SELECT
        #{recId},
        #{recNo},
        #{recQty},
        #{sku}
    FROM DUAL
    WHERE NOT EXISTS(
            SELECT 'X'
            FROM inter_wta_warehouse_purchase_goods
            WHERE rec_no = #{recNo} AND rec_id = #{recId}
    );

mapper.update:
UPDATE inter_wta_order_shipping_header


        send_flag = #{sendFlag},


        send_time = #{sendTime},


        error_message = #{errorMessage}


WHERE task_no = #{taskNo} AND order_no = #{orderNo}
