mapper.select:
SELECT *
FROM td_order_copy
WHERE main_order_number = #{orderNumber}

SELECT *
FROM ord_base_info
WHERE ord_no = #{orderNumber}

SELECT *
FROM ord_base_info
LIMIT #{size}, 1000;

SELECT *
FROM td_order_goods_copy
WHERE td_order_id = #{tdOrderId};

SELECT EXISTS(
        SELECT 1
        FROM ord_goods_info
        WHERE ord_no = #{orderNo} AND gid = #{gid} AND goods_line_type = #{goodsLineType}
)

SELECT *
FROM td_order_goods_copy
WHERE td_order_id = #{id}
